import json
import os


class Coordinaat:
    def __init__(self, x, y):
        self.x = x
        self.y = y


class Schatkaart:
    def __init__(self, path):
        # vanaf hier wordt de file geladen en geparset
        with open(path) as f:
            content = f.readlines()

        content = [x.strip() for x in content]

        start_punt, *instructies = content

        start_punt = json.loads(start_punt)

        self.start_punt = Coordinaat(start_punt[1], start_punt[0])
        self.eind_punt = Coordinaat(start_punt[1], start_punt[0])
        self.instructies = instructies
        # hier is alles ingevoerd uit de .txt file

    def vind_eindpunt(self):
        # voor elke instuctie de acties uitvoeren. self.instructies is een list instructies
        for instructie in self.instructies:
            # richting aangeven: x of y?
            if 'x' in instructie:
                waarde = int(instructie[1:3])
                self.eind_punt.x = self.eind_punt.x + waarde
            if 'y' in instructie:
                waarde = int(instructie[1:3])
                self.eind_punt.y = self.eind_punt.y + waarde


