def is_land(schatkaart):
    schatkaart.vind_eindpunt()
    if not 0 <= schatkaart.eind_punt.x < 40:
        return False
    elif not 0 <= schatkaart.eind_punt.y < 30:
        return False
    else:
        if eiland[schatkaart.eind_punt.y][schatkaart.eind_punt.x] == 1:
            return True
        else:
            return False

# pak de lijst van 'paths' naar de kaarten
schatkaarten_lijst = []

for root, directory, files in os.walk("treasure_maps/"):
    schatkaarten_lijst = files

count = 0

# voor elk 'path' in de padenlijst
for schatkaart_path in schatkaarten_lijst:
    schatkaart = Schatkaart("treasure_maps/" + schatkaart_path)

    if is_land(schatkaart):
        count += 1
        print("ja:", schatkaart_path, schatkaart.eind_punt.x, schatkaart.eind_punt.y)

